import { Component } from '@angular/core';

@Component({
  selector: 'app-exercicio01',
  templateUrl: './exercicio01.component.html',
  styleUrls: ['./exercicio01.component.css']
})
export class Exercicio01Component {
  nome: string = '';
}
<div>
  <h2>Exercício 01</h2>
  <label for="nome">Digite o seu nome</label><br>
  <input type="text" id="nome" [(ngModel)]="nome">
  <button (click)="alert(nome)">Abrir prompt</button>
</div>
