import { Component } from '@angular/core';

@Component({
  selector: 'app-ex01',
  templateUrl: './ex01.component.html',
  styleUrls: ['./ex01.component.css']
})
export class Ex01Component {
  n1: number = 0
  n2: number = 0
}
<div>
  <h2>Exercício 01</h2>
  <input type="number" placeholder="Valor 1" [(ngModel)]="n1">
  <span> + </span>
  <input type="number" placeholder="Valor 2" [(ngModel)]="n2">
  <p>A soma entre os valores é de <strong>{{ n1 + n2 }}</strong></p>
</div>
