import { Component } from '@angular/core';

@Component({
  selector: 'app-exercicio04',
  templateUrl: './exercicio04.component.html',
  styleUrls: ['./exercicio04.component.css']
})
export class Exercicio04Component {
  nome: string = '';
  telefone: string = '';
  email: string = '';
  prefTelefone: boolean = false;
  prefWhatsapp: boolean = false;
  prefEmail: boolean = false;
}
<div>
  <h2>Exercício 04</h2>
  <label for="nome">Nome</label><br>
  <input type="text" id="nome" [(ngModel)]="nome"><br>
  <label for="telefone">Telefone</label><br>
  <input type="text" id="telefone" [(ngModel)]="telefone"><br>
  <label for="email">E-mail</label><br>
  <input type="text" id="email" [(ngModel)]="email"><br>
  <label for="">Preferências de contato</label>
  <input type="checkbox" id="prefTelefone">
  <label for="prefTelefone">Telefone</label>
  <input type="checkbox" id="prefWhatsapp">
  <label for="prefWhatsapp">Whatsapp</label>
  <input type="checkbox" id="prefEmail">
  <label for="prefEmail">E-mail</label><br><br>
  <button (click)="exibirResumo"></button>
</div>
