import { Component } from '@angular/core';

@Component({
  selector: 'app-exercicio03',
  templateUrl: './exercicio03.component.html',
  styleUrls: ['./exercicio03.component.css']
})
export class Exercicio03Component {
  empresa: string = '';
}
<div>
  <h2>Exercício 03</h2>
  <label for="curso">Selecione uma empresa</label>
  <select id="curso" [(ngModel)]="empresa">
    <option value="Sistemas para internet">Sistemas para internet</option>
    <option value="Análise e desenvolvimento de sistemas">Análise e desenvolvimento de sistemas</option>
    <option value="Ciência da computação">Ciência da computação</option>
    <option value="Engenharia de computação">Engenharia de computação</option>
  </select><br>
</div>
