import { Component } from '@angular/core';

@Component({
  selector: 'app-date',
  templateUrl: './date.component.html',
  styleUrls: ['./date.component.css']
})
export class DateComponent {
  data: Date = new Date();

  setInterval(() => {
    this.data = new Date();
  }, 1000);


}
<div>
  <h2>Data e Hora</h2>
  <p>{{ data | date: 'dd/MM/yyyy HH:mm:ss' }}</p>
</div>
