import { Component } from '@angular/core';

@Component({
  selector: 'app-exercicio04',
  templateUrl: './exercicio04.component.html',
  styleUrls: ['./exercicio04.component.css']
})
export class Exercicio04Component {
  caminhoImagem: string = '';

  trocarImagem(dog: string){
    this.caminhoImagem = 'assets/'+ dog + '.png'
  }
}
<div>
  <h2>Exercício 04</h2>
  <button (click)="trocarImagem('doberman')">Doberman</button>
  <button (click)="trocarImagem('chihuahua')">Chihuahua</button>
  <button (click)="trocarImagem('viralata')">Vira lata</button>
  <br>
  <img [src]="caminhoImagem" alt="Imagem do cachorro">
</div>
