import { Component } from '@angular/core';

@Component({
  selector: 'app-exercicio03',
  templateUrl: './exercicio03.component.html',
  styleUrls: ['./exercicio03.component.css']
})
export class Exercicio03Component {
  empresa: string = '';
}
<div>
  <h2>Exercício 03</h2>
  <label for="curso">Selecione uma empresa</label>
  <select id="curso" [(ngModel)]="empresa">
    <option value="kitkat.webp">Kit Kat</option>
    <option value="burguer-king.webp">Burguer King</option>
    <option value="nike.webp">Nike</option>
  </select><br>
  <img src="{{ 'assets/' + empresa }}" alt="Logomarca">
</div>
