import { Component } from '@angular/core';

@Component({
  selector: 'app-ex03',
  templateUrl: './ex03.component.html',
  styleUrls: ['./ex03.component.css']
})
export class Ex03Component {
  curso: string = '';
  candidato: string = '';
}
<div>
  <h2>Exercício 03</h2>
  <label for="curso">Selecione um curso</label>
  <select id="curso" [(ngModel)]="curso">
    <option value="Sistemas para internet">Sistemas para internet</option>
    <option value="Análise e desenvolvimento de sistemas">Análise e desenvolvimento de sistemas</option>
    <option value="Ciência da computação">Ciência da computação</option>
    <option value="Engenharia de computação">Engenharia de computação</option>
  </select>
  <label for="candidato">Informe o seu nome</label>
  <input type="text" id="candidato" [(ngModel)]="candidato">
  <div>
    <span class="bold">Candidato: </span><span>{{ candidato }}</span>
    <span class="bold">Curso selecionado: </span><span>{{ curso }}</span>
  </div>
</div>
