import { Component } from '@angular/core';

@Component({
  selector: 'app-exercicio07',
  templateUrl: './exercicio07.component.html',
  styleUrls: ['./exercicio07.component.css']
})
export class Exercicio07Component {

}
<div>
  <h2>Exercício 07</h2>
  <button (click)="trocarCor('doberman')">Doberman</button>
  <button (click)="trocarCor('chihuahua')">Chihuahua</button>
  <button (click)="trocarCor('viralata')">Vira lata</button>
  <br>
  <img [src]="caminhoImagem" alt="Imagem do cachorro">
</div>
