import { Component } from '@angular/core';

@Component({
  selector: 'app-exercicio07',
  templateUrl: './exercicio07.component.html',
  styleUrls: ['./exercicio07.component.css']
})
export class Exercicio07Component {
  corDiv: string = '';

  trocarCor(cor: string): void{
    this.corDiv = 'background-color: ' + cor;
  }
}
<div>
  <h2>Exercício 07</h2>
  <button (click)="trocarCor('#F00')">Vermelho</button>
  <button (click)="trocarCor('#0F0')">Verde</button>
  <button (click)="trocarCor('#00F')">Azul</button>
  <br>
  <div class="divColorida" [style]="corDiv"></div>
</div>
